import React from 'react'
import { useState } from 'react'
import "../CSS/Pages/Bespoke.css"
export default function Bespoke() {
  const [MultiStepFormNumber,SetMultiStepFormNumber]=useState(0)
  function ForwardStep(InputValue)
  {
    if(InputValue===0)
    {
      SetMultiStepFormNumber(1);
    }
    else if(InputValue===1)
    {
      SetMultiStepFormNumber(2);
    }
    else if(InputValue===2)
    {
      SetMultiStepFormNumber(3);
    }
    else if(InputValue===3)
    {
      SetMultiStepFormNumber(4);
    }
    else if(InputValue===4){
      SetMultiStepFormNumber(5);
    }
  }
  function BackwordStep(InputValue)
  {
    if(InputValue===0)
    {
      SetMultiStepFormNumber(0);
    }
    else if(InputValue===1)
    {
      SetMultiStepFormNumber(0);
    }
    else if(InputValue===2)
    {
      SetMultiStepFormNumber(1);
    }
    else if(InputValue===3)
    {
      SetMultiStepFormNumber(2);
    }
    else if(InputValue===4){
      SetMultiStepFormNumber(3);
    }
  }
  /**
 * Define a function to navigate betweens form steps.
 * It accepts one parameter. That is - step number.
 */
const navigateToFormStep = (stepNumber) => {
  /**
   * Hide all form steps.
   */
  document.querySelectorAll(".form-step").forEach((formStepElement) => {
      formStepElement.classList.add("d-none");
  });
  /**
   * Mark all form steps as unfinished.
   */
  document.querySelectorAll(".form-stepper-list").forEach((formStepHeader) => {
      formStepHeader.classList.add("form-stepper-unfinished");
      formStepHeader.classList.remove("form-stepper-active", "form-stepper-completed");
  });
  /**
   * Show the current form step (as passed to the function).
   */
  document.querySelector("#step-" + stepNumber).classList.remove("d-none");
  /**
   * Select the form step circle (progress bar).
   */
  const formStepCircle = document.querySelector('li[step="' + stepNumber + '"]');
  /**
   * Mark the current form step as active.
   */
  formStepCircle.classList.remove("form-stepper-unfinished", "form-stepper-completed");
  formStepCircle.classList.add("form-stepper-active");
  /**
   * Loop through each form step circles.
   * This loop will continue up to the current step number.
   * Example: If the current step is 3,
   * then the loop will perform operations for step 1 and 2.
   */
  for (let index = 0; index < stepNumber; index++) {
      /**
       * Select the form step circle (progress bar).
       */
      const formStepCircle = document.querySelector('li[step="' + index + '"]');
      /**
       * Check if the element exist. If yes, then proceed.
       */
      if (formStepCircle) {
          /**
           * Mark the form step as completed.
           */
          formStepCircle.classList.remove("form-stepper-unfinished", "form-stepper-active");
          formStepCircle.classList.add("form-stepper-completed");
      }
  }
};
/**
* Select all form navigation buttons, and loop through them.
*/
document.querySelectorAll(".btn-navigate-form-step").forEach((formNavigationBtn) => {
  /**
   * Add a click event listener to the button.
   */
  formNavigationBtn.addEventListener("click", () => {
      /**
       * Get the value of the step.
       */
      const stepNumber = parseInt(formNavigationBtn.getAttribute("step_number"));
      /**
       * Call the function to navigate to the target form step.
       */
      navigateToFormStep(stepNumber);
  });
});
  return (
   <>
   <div className="container-fluid BespokeContainer">
    <div className='row mx-md-'>
    <div>
    <h1>Create a Multi-Step Form Using HTML, CSS, and JavaScript</h1>
    <div id="multi-step-form-container " className='a'>
        {/* <!-- Form Steps / Progress Bar --> */}
        <ul className="form-stepper form-stepper-horizontal text-center mx-auto pl-0">
            {/* <!-- Step 1 --> */}
            <li className="form-stepper-active text-center form-stepper-list" step="1">
                <a className="mx-2">
                    <span className="form-stepper-circle">
                        <span>1</span>
                    </span>
                    <div className="label">Account Basic Details</div>
                </a>
            </li>
            {/* <!-- Step 2 --> */}
            <li className="form-stepper-unfinished text-center form-stepper-list" step="2">
                <a className="mx-2">
                    <span className="form-stepper-circle text-muted">
                        <span>2</span>
                    </span>
                    <div className="label text-muted">Social Profiles</div>
                </a>
            </li>
            {/* <!-- Step 3 --> */}
            <li className="form-stepper-unfinished text-center form-stepper-list" step="3">
