            {/* <!-- Step 2 Content, default hidden on page load. --> */}
            <section id="step-2" className="form-step d-none">
                <h2 className="font-normal">Social Profiles</h2>
                {/* <!-- Step 2 input fields --> */}
                <div className="mt-3">
                    Step 2 input fields goes here..
                </div>
                <div className="mt-3">
                    <button className="button btn-navigate-form-step" type="button" step_number="1">Prev</button>
                    <button className="button btn-navigate-form-step" type="button" step_number="3">Next</button>
                </div>
            </section>
            {/* <!-- Step 3 Content, default hidden on page load. --> */}
            <section id="step-3" className="form-step d-none">
                <h2 className="font-normal">Personal Details</h2>
                {/* <!-- Step 3 input fields --> */}
                <div className="mt-3">
                    Step 3 input fields goes here..
                </div>
                <div className="mt-3">
                    <button className="button btn-navigate-form-step" type="button" step_number="2">Prev</button>
